import { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import "../css/payments.min.css"
import StripeCheckout from 'react-stripe-checkout'
import {
  CardElement,
  Elements,
  useElements,
  useStripe
} from '@stripe/react-stripe-js';
import axios from 'axios';
// Custom styling can be passed to options when creating an Element.
import React, { Component } from 'react'

class Payments extends Component {
    state= {
        token1: '',
        addresses1: ''

      }
    render() {
        const lol = () =>{
            console.log("wywołanie funckji")
        }
        const onToken = () => {
            
            console.log("proba kliknięcia")
            var obj = { name: "John", age: 30, city: "New York" };
            obj1= JSON.stringify(obj)
            console.log(obj)
            fetch('http://127.0.0.1:8000/charge/', {
              method: 'POST',
              body: JSON.stringify(obj),
            }).then(response => {
              response.json().then(data => {
                alert(`We are in business, ${data.name}`);
              });
            });
          }
        

        function handleToken(token,addresses){
            console.log(token,addresses)
            this.setState({
                token1: token,
                addresses1: addresses
            })
        }

        
        return (
            <div className="payments">
                <h1>Płatności</h1>
                <h1>Płatności</h1>
                <button type="submit" onClick={onToken}>Proba send request</button>
                <StripeCheckout 
                stripeKey="pk_test_EsHEu4kQeXtUp2or3usiLzuu00MbZjNI5d"
                // token={handleToken}
                token={this.onToken}
                billingAddress
                shippingAddress
                amount={100*100}
                name="Zapłata"/>
            </div>
            
        )
    }
}

export default Payments
