import React, { Component } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { connect } from "react-redux";
import BaseRouter from "./routes";
import * as actions from "./store/actions/auth";
import "semantic-ui-css/semantic.min.css";
import CustomLayout from "./containers/Layout";
import {getItems} from "./store/actions/items";
import BaseLayout from "./containers/BaseLayout";
class App extends Component {
  componentDidMount() {
    this.props.onTryAutoSignup();
    this.props.getItems();
    // console.log(this.props.isLoadedItems)
    
    if(this.props.isLoadedItems){
      console.log("działa")
    }else{
      console.log("nie działa")
    }
  }

  render() {
    // console.log(this.props.isLoadedItems)
    return (
      <Router>
        {this.props.isLoadedItems ?
        <CustomLayout {...this.props}>
          <BaseRouter />
        </CustomLayout> : <h1>Ładowanie</h1>}
      </Router>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null,
    isLoadedItems: state.items.isLoadeditems,
    items: state.items.items,
    mainarray : state.items.mainarray.items

  };
};

const mapDispatchToProps = dispatch => {
  return {
    onTryAutoSignup: () => dispatch(actions.authCheckState()),
    getItems: () =>dispatch(getItems())
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
