import axios from "axios";
import * as actionTypes from "./actionTypes";
export const getAllItemsStart = () =>{
  return {
    type: actionTypes.GET_ALL_ITEMS_START
  };
}
export const getArray = (items) =>{
  return{
    type: actionTypes.GET_ARRAY,
    items
  }
}
export const getAllItemsSuccess = (items) => {
  return {
    type: actionTypes.GET_ALL_ITEMS_SUCCESS,
    items: items
  };
};
export const getAllItemsFail = error => {
  return {
    type: actionTypes.GET_ALL_ITEMS_FAIL,
    error: error
  };
};  
export const getAllItems = (id) => {
  return dispatch => {
      dispatch(getItemsStart());
      axios.get(`http://127.0.0.1:8000/api/products/${id}`)
      .then(res => {
          const items= res.data
          dispatch(getItemsSuccess(items));
      })
      .catch(err => {
      //   dispatch(getItemsFail());
      });
  };
};
export const getItemsStart = () => {
    return {
      type: actionTypes.GET_ITEMS_START
    };
  };
export const getItemsSuccess = (items) => {
    return {
      type: actionTypes.GET_ITEMS_SUCCESS,
      items: items
    };
  };
export const getItemsFail = error => {
    return {
      type: actionTypes.GET_ITEMS_FAIL,
      error: error
    };
  };  

  export const getItems = () => {
    return dispatch => {
        dispatch(getItemsStart());
        axios.get("http://127.0.0.1:8000/api/products/")
        .then(res => {
            const items= res.data
            console.log(items)
            // console.log(items[0])
            let array = []
            for(let i=0; i<items.length;i++){
              let url= items[i].url
              axios.get(url).then(res =>
                {
                  // console.log(res.data)
                  let length = res.data.children.length
                  if(length > 1){
                    // array.push(res.data)
                    array.unshift(res.data)
                  }
                }
              )
              
            }
            console.log(array)
            
            dispatch(getArray(array));
            dispatch(getItemsSuccess(items));
        })
        .catch(err => {
        //   dispatch(getItemsFail());
        });
    };
  };
