import { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import "../css/payments.min.css"
import StripeCheckout from 'react-stripe-checkout'
import axios from 'axios'
import {toast } from 'react-toastify';
import {
  CardElement,
  Elements,
  useElements,
  useStripe
} from '@stripe/react-stripe-js';

// Custom styling can be passed to options when creating an Element.
import React, { Component } from 'react'
import Axios from 'axios';
toast.configure()
class Payments extends Component {
    
    render() {
        async function handleToken(token,addresses){
            console.log(token,addresses)
            const response = await axios.post('http://127.0.0.1:8000/charge/',{token,addresses})
            const status = response.data
            if(status === 'success'){
                toast('Success! check emails for details',{type: 'success'})
            }
        }
        return (
            <div className="payments">
                <h1>Płatności</h1>
                <h1>Płatności</h1>
                <StripeCheckout 
                stripeKey="pk_test_EsHEu4kQeXtUp2or3usiLzuu00MbZjNI5d"
                token={handleToken}
                billingAddress
                shippingAddress
                amount={100*100}
                name="nazwa prduktu"/>
            </div>
            
        )
    }
}
export default Payments
